package com.chenjin.pattern.adapter;/** * 类适配器模式（adapter pattern） * 电源适配器 * 可以将220V或者其他电压的转换成5V * */public class PowerAdapter extends PowerAdaptee220V implements PowerTarget{    @Override    public int output5V() {        int a=output()/ 44;        System.out.println("输出电压为====="+a);        return a;    }    public static void main(String[] args) {        PowerAdapter powerAdapter = new PowerAdapter();        powerAdapter.output5V();    }}