package com.chenjin.pattern.adapter;/** * 对象适配器模式（object adapter pattern） */public class PowerAdapter2 implements PowerTarget{    private PowerAdaptee220V powerAdaptee220V;    public PowerAdapter2(PowerAdaptee220V powerAdaptee220V) {        super();        this.powerAdaptee220V = powerAdaptee220V;    }    @Override    public int output5V() {        int output = powerAdaptee220V.output220V();        System.out.println("电源适配器开始工作，此时输出电压是：" + output);        output = output/44;        System.out.println("电源适配器工作完成，此时输出电压是：" + output);        return output;    }    public static void main(String[] args) {        PowerAdaptee220V powerAdaptee220V = new PowerAdaptee220V();        PowerAdapter2 powerAdapter2 = new PowerAdapter2(powerAdaptee220V);        powerAdapter2.output5V();    }}