package com.chenjin.pattern.adapter;/** * 类适配器模式（adapter pattern） * 电源适配器 * 可以将220V或者其他电压的转换成5V * 220V或110V需要实现 PowerInterface接口 * 在适配器中根据用户传入的type进行逻辑的编写判定 * */public class PowerAdapter3 implements PowerTarget{    PowerInterface powerInterface;    private  String type;    public PowerAdapter3(String type){        this.type=type;        if(type.equals("220V")){            this.powerInterface=new PowerAdaptee220V();        }else if(type.equals("110V")){            this.powerInterface=new PowerAdaptee110V();        }    }    @Override    public int output5V() {        int a=0;        if(type.equals("110V")){             a = powerInterface.output() / 22;        }else if(type.equals("220V")){             a = powerInterface.output() / 44;        }        System.out.println("转换后电压为==="+a);        return a;    }    public static void main(String[] args) {        PowerAdapter3 powerAdapter = new PowerAdapter3("220V");        powerAdapter.output5V();    }}